/* Alpine.js Minimal Stub for Offline Mode */

window.Alpine = {
    data: function(callback) {
        return callback;
    },
    
    component: function(name, callback) {
        return callback;
    },
    
    store: function(name, data) {
        window[name] = data;
        return data;
    },
    
    directive: function(name, callback) {
        return callback;
    },
    
    throttle: function(fn, wait) {
        return fn;
    },
    
    debounce: function(fn, wait) {
        return fn;
    },
    
    nextTick: function(callback) {
        if(typeof callback === 'function') {
            setTimeout(callback, 0);
        }
    },
    
    reactive: function(data) {
        return data;
    }
};

// Prevent Alpine from trying to auto-initialize
document.addEventListener('DOMContentLoaded', function() {
    // Alpine.js initialization would go here
    // In offline mode, we just provide basic support
    console.log('Alpine.js stub loaded for offline compatibility');
});
