/* SweetAlert2 Minimal JavaScript Stub for Offline Mode */

window.Swal = {
    fire: function(config) {
        // Simple fallback for offline mode
        var message = config.text || config.title || '';
        var type = config.icon || 'info';
        
        // Show a simple browser alert
        var prefix = '';
        switch(type) {
            case 'success': prefix = '✓ '; break;
            case 'error': prefix = '✗ '; break;
            case 'warning': prefix = '⚠ '; break;
            case 'info': prefix = 'ℹ '; break;
        }
        
        alert(prefix + message);
        
        // Return a promise-like object for compatibility
        return {
            then: function(fn) {
                if(typeof fn === 'function') fn();
                return this;
            },
            catch: function(fn) {
                return this;
            }
        };
    },
    
    mixin: function(config) {
        return Swal;
    },
    
    isVisible: function() {
        return false;
    },
    
    close: function() {
        return this;
    }
};

// For older code that might use this pattern
if (typeof window.swal === 'undefined') {
    window.swal = window.Swal;
}
